import { useEffect, useState } from 'react';
import { Button, StyleSheet, Text, View, TextInput, Image, TouchableOpacity, TouchableWithoutFeedback } from 'react-native';
import Logo from '../assets/logo.png'
import CustomInput from '../components/CustomInput';
import styles from '../styles/styles';
import app from '../FirebaseConfig';
import { createUserWithEmailAndPassword, signInWithEmailAndPassword, getAuth } from '@firebase/auth';
import { useNavigation } from '@react-navigation/native'

export default function Login() {
    const auth = getAuth(app)
    const [email, setEmail] = useState()
    const [senha, setSenha] = useState()
    const navigation = useNavigation()

    useEffect(() => {
        console.log(auth.currentUser)
        if (auth.currentUser)
            navigation.navigate('Listagem')
    }, (auth.currentUse))

    const criarUsuario = () => {
        createUserWithEmailAndPassword(auth, email, senha)
            .then((userCredentials) => {
                console.log(userCredentials)
            }).catch((err) => {
                console.error(err)
            })
    }

    return (
        <View style={styles.container}>
            <CustomInput
                placeholder='Valor'
                value={email}
                funcao={setEmailInputValue}
            />

            <CustomInput
                placeholder='Referência'
                value={email}
                funcao={setEmailInputValue}
            />

            <CustomInput
                placeholder='Data'
                value={email}
                funcao={setEmailInputValue}
                
            />

            <TouchableOpacity style={{ marginTop: 15 }} onPress={criarUsuario}>
                <View style={styles.button}>
                    <Text style={styles.buttonText}>Criar Transação</Text>
                </View>
            </TouchableOpacity>
        </View>
    );
}
